package incrementdecreoperators;

public class Test04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;//0
		int j=2;//1
		int k=3;//2
		
		int m=i-- -j-- -k--;//1-2-3//-4
		System.out.println("i=" +i);
		System.out.println("j=" +j);
		System.out.println("k=" +k);
		System.out.println("m=" +m);

	}

}
