package javasessions;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1 to 10:
		//1. while loop:
		
		int i =1;
		while(i<=100)
		{
			System.out.println(i);
			i++;
		}
		
		int l=1;
		while(l<=100)
		{
			if(l==5) {
				System.out.println("hi");
			}
			System.out.println(l);
			l++;
		}
	}

}
